INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Användning', 'Allmän plats', 'Enskilt', 'Väg', NULL, 'Väg', 'Väg', NULL, 'Väg är en generell användningsbestämmelse som bör tillämpas för väg som främst är avsedd för trafik till, från och/eller genom en tätort samt mellan olika områden inom en tätort. Väg preciseras endast om det finns särskilt behov av det.', 'VÄG#', 'PBL (2010:900)', '4', '8', '1', '2', 'Väg är en generell användningsbestämmelse som bör tillämpas för de stråk som ingår i det sammanhängande huvudnätet och som främst avser långväga trafik till och från eller genom en tätort. En väg har hög framkomlighet, stort korsningsavstånd, stort trafikflöde och få utfarter. Andelen tung trafik är hög. I de flesta fall är det kommunen som är huvudman för vägar. Trafikverket kan vara väghållare.', 19, 'Ljust grå', '2015-01-02', NULL, 'DP_AP_En_Vag', '2017-03-01', '2016-03-07', 'f95bfeaf-d5ce-4f05-aec8-4358b7559c6e', NULL, NULL, NULL, NULL, NULL);
