INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Användning', 'Allmän plats', 'Kommunalt', 'Gata', NULL, 'Huvudgata', 'Huvudgata', NULL, 'Huvudgata är en preciserad bestämmelse som innebär att endast denna form av användning är tillåten.', 'GATA#', 'PBL (2010:900)', '4', '5', '1', '2', 'Huvudgata är en precisering av användningsslaget Gata. Endast den preciserade formen av användning är tillåten. En huvudgata ingår i lokalnätet och binder ihop lokalgator inom en tätort. Huvudgatan är avsedd för alla trafikslag. Huvudgatan har en högre framkomlighet för biltrafiken än lokalgatorna även om utfarterna kan ligga tätt. I de flest fall är det kommunen som är huvudman för gator.', 19, 'Ljust grå', '2015-01-02', NULL, 'DP_AP_Ko_Hgata', '2016-03-07', '2016-03-07', '426e74e8-58b4-432b-a6ca-876fff456ab3', NULL, NULL, NULL, NULL, NULL);
