INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Användning', 'Kvartersmark', NULL, 'Tillfällig vistelse', NULL, 'Tillfällig vistelse', 'Tillfällig vistelse', NULL, 'Tillfällig vistelse är en generell användningsbestämmelse som bör tillämpas för områden för alla typer av tillfällig övernattning samt konferenslokaler.', 'O', 'PBL (2010:900)', '4', '5', '1', '3', 'Tillfällig vistelse är en generell användningsbestämmelse som bör tillämpas för områden för alla typer av tillfällig övernattning samt konferenslokaler. Tillfällig vistelse innefattar en rad olika typer av verksamheter som exempelvis hotell, motell, vandrarhem och pensionat. Verksamheterna drivs med stöd av annan lagstiftning än de verksamheter som kallas bostäder. Dessa speciallagstiftningar anger då exempelvis särskilda krav på utformning och disposition av lokaler, krav på organisation och personal osv. De olika typerna av tillfälliga boenden kan bedömas ha en annan omgivningspåverkan än bostadsanvändning och i många fall anses olika typer av tillfälliga boenden också ställa andra krav på omgivningen än normal bostadsanvändning. Beroende på vilken typ av tillfälligt boende det handlar om ställs det också särskilda krav på den tekniska utformningen av byggnaderna som skiljer sig från de krav som ställs på en byggnad för bostäder.', 9, 'Orange', '2015-01-02', NULL, 'DP_KM_O', '2016-03-07', '2016-03-07', 'a82d6223-17eb-49a6-9470-307703fe156f', NULL, NULL, NULL, NULL, NULL);
