INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Placering', NULL, NULL, 'Marken får inte förses med [byggnad:text]', NULL, 'Prickmark är en generell bestämmelse som anger att marken inte får förses med byggnad. Om det är nödvändigt med hänsyn till planens syfte kan typen av begränsning preciseras.', NULL, 'PBL (2010:900)', '4', '16', '1', '1', 'Denna bestämmelse om byggandets omfattning inom kvartersmark används då den generella bestämmelsen om att marken inte får förses med byggnad behöver preciseras närmare.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Plac_Prickmark_A', '2016-03-07', '2016-03-07', 'f6f1915f-c89b-4ead-a139-07c302f6b1db', 'symbol', '2017-02-21T13:07:44.2826847', 18, 'Prickmark', NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Användning', NULL, NULL, 'Byggnadens [delar:text] ska inte [användning:text]', NULL, 'Denna bestämmelse finns inte med i Boverkets allmänna råd. Tidigare har denna typ av bestämmelse - exempelvis att handel endast tillåts i bottenvåning - eller liknande bestämmelser, uttryckts som en precisering av en användningsbestämmelse. Men lagstödet för denna typ av bestämmelser är 4 kap. 11 § 2 p - om byggnaders användning och bör snarast ses som en egenskapsbestämmelse. Boverket avser att justera de allmänna råden enligt detta.', 'e#', 'PBL (2010:900)', '4', '11', '1', '2', 'Bestämmelser om hur en byggnad får användas kan anges. Sådana bestämmelser tillämpas då exempelvis handel endast är tillåtet i bottenvåningen på en byggnad.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Anvandning_Ej_Byggnad_A', '2017-03-01', '2017-03-01', 'b9ff0229-2836-4fd3-ac5a-c5ca1b193be4', NULL, NULL, NULL, NULL, NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Fastighet', NULL, NULL, 'Minsta fastighetsstorlek är [storlek:decimaltal] kvadratmeter', 'Min', 'Bestämmelser om största och minsta fastighetsstorlek kan anges.', 'd#', 'PBL (2010:900)', '4', '18', '1', NULL, 'Bestämmelser om största och minsta fastighetsstorlek används för att styra exploateringsgraden inom ett område. Om fastighetsindelningen behöver regleras med i detalj kan fastighetsbestämmelser, som är administrativa bestämmelser, användas.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Fastighet_Min_Fastighet', '2017-02-21', '2016-03-07', '4ca4281b-b3a7-4501-a642-252570a2eb07', NULL, NULL, NULL, NULL, NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Fastighet', NULL, NULL, 'Största fastighetsstorlek är [storlek:decimaltal] kvadratmeter', 'Max', 'Bestämmelser om största och minsta fastighetsstorlek kan anges.', 'd#', 'PBL (2010:900)', '4', '18', '1', NULL, 'Bestämmelser om största och minsta fastighetsstorlek används för att styra exploateringsgraden inom ett område. Om fastighetsindelningen behöver regleras med i detalj kan fastighetsbestämmelser, som är administrativa bestämmelser, användas.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Fastighet_St_Fastighet', '2017-02-21', '2016-03-07', 'd74dc5ed-6b96-4edd-b635-949299cd2886', NULL, NULL, NULL, NULL, NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Placering', NULL, NULL, 'Byggnad ska placeras minst [avstand:decimaltal] meter från tomtgräns', 'Min', 'Bestämmelser om placering reglerar byggnadsverks och tomters placering.', 'p#', 'PBL (2010:900)', '4', '16', '1', '1', 'Bestämmelser om placering av byggnadsverk och tomter används ofta för att reglera byggnaders placering på tomten i förhållande till grannfastigheter och i förhållande till gata och omgivning. I tät bebyggelse med små fastigheter är denna typ av bestämmelser viktiga för att få en fungerande bebyggelse. Det är viktigt att samordna placeringsbestämmelser med andra bestämmelser om exploatering och fastighetsindelning. Placering kan även regleras genom begränsning av markens utnyttjande för byggnader, till exempel prickmark.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Plac_Byggnad_Avstand_Tomtgrans', '2016-03-07', '2016-03-07', 'd18a54e1-375f-4955-8bd8-b1ed11f5fa79', NULL, NULL, NULL, NULL, NULL);
