INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Mark', NULL, NULL, 'Vegetationen [vegetation:text]', NULL, 'Bestämmelser om markens anordnande och vegetation reglerar markförhållanden, vegetation och parkering.', 'n#', 'PBL (2010:900)', '4', '10', NULL, NULL, 'Med vegetation avses här träd, buskar och liknande, men inte grödor, bete och liknande. Av proposition till den äldre PBL (1987:10) se nedan, framgår det att bestämmelsen främst är tänkt att utnyttjas för att reglera vegetation som påverkar till exempel trafiksäkerhet eller solinstrålning. Däremot var inte avsikten att man utifrån den bestämmelsen skulle kunna detaljreglera till exempel markbeläggningar eller annan vegetation än träd. (Proposition 1985/86:1 s 583 f och s 298 f.)När det gäller träd ger dock bestämmelsen utrymme för att skydda för bebyggelsemiljön värdefulla träd. Inför man ett skydd för träd, bör man oftast kombinera detta med en utökad marklovsplikt för trädfällning för att kunna kontrollera efterlevnaden av bestämmelsen.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Mark_Vegetation_A', '2017-03-01', '2017-03-01', 'e7d5e4fc-bfe5-4ad5-9048-6279d124f2d9', NULL, NULL, NULL, NULL, NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Mark', NULL, NULL, 'Parkering [anordnande:text]', NULL, 'Bestämmelser om markens anordnande och vegetation reglerar markförhållanden, vegetation och parkering.', 'n#', 'PBL (2010:900)', '4', '13', '1', '1', 'Bestämmelser om markens anordnande och vegetation reglerar markförhållanden, vegetation och parkering. Bestämmelser kan införas för att garantera att det på tomten eller i dess närhet i skälig utsträckning ska finnas lämpligt utrymme för parkering, lastning och lossning av fordon.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Mark_Parkering', '2016-03-07', '2016-03-07', 'ba3a919a-837e-484a-979a-52ff2fd7dd45', NULL, NULL, NULL, NULL, NULL);
