INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Utfart', NULL, NULL, 'Körbar förbindelse får inte anordnas', NULL, 'Stängsel, utfart och annan utgång mot allmän plats kan regleras.', NULL, 'PBL (2010:900)', '4', '9', NULL, NULL, 'Bestämmelser om stängsel, utfart och annan utgång mot allmän plats kan regleras. Med utfart menas både ut- och infart. Kommunen kan genom föreläggande kräva att bestämmelserna följs oavsett om lov söks för någon åtgärd inom fastighet eller inte. Detta gäller dock enbart om det behövs med hänsyn till trafiksäkerheten. Föreläggandet innebär inte någon rätt till ersättning. Om en befintlig utfart bör ändras i samband med någon annan lovpliktig åtgärd kan ändringen sättas som villkor för att kommunen ska ge lov till väsentligt ändrad markanvändning.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Utfart_EjUtfart', '2016-03-07', '2016-03-07', '7844199c-37d3-4fa2-a9bf-a361475d9dea', 'symbol', '2017-02-21T13:07:44.4596768', 15, 'Utfart får inte finnas', NULL);
