INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Utfart', NULL, NULL, '[utfart:text] får inte anordnas', NULL, 'Stängsel, utfart och annan utgång mot allmän plats kan regleras.', NULL, 'PBL (2010:900)', '4', '9', NULL, NULL, 'Denna bestämmelse om att utfart inte får anordnas används då ingen annan angiven egenskapsbestämmelse om utfart i katalogen är tillämpbar.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Utfart_EjUtfart_A', '2016-03-07', '2016-03-07', '77decc70-c23f-48f4-88d9-38f1fd8b3b72', 'symbol', '2017-02-21T13:07:44.4596768', 15, 'Utfart får inte finnas', NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Kulturvärden', NULL, NULL, 'Bebyggelseområdets karaktär vad gäller [kulturvarde:text] ska bevaras', NULL, 'Skydd av kulturvärden kan enbart anges för befintliga värdefulla byggnadsverk, bebyggelseområden och tomter.', 'q#', 'PBL (2010:900)', '4', '16', '1', '3', 'Bestämmelser om skydd för befintliga värdefulla byggnadsverk, bebyggelseområden och tomter kan införas. Bestämmelserna kan omfatta både exteriör och interiör liksom byggnadsdetaljer såväl som viktig växtlighet och konstarbeten. Om det är motiverat kan kraven även omfatta byggnadsmaterial och byggnadsteknik vid underhållsåtgärder på byggnader och anläggningar samt viss skötsel av vegetation. Skyddsbestämmelser bör alltid preciseras så att det tydligt framgår vad skyddet avser. Skyddsåtgärder bör alltid kombineras med utökad lovplikt för åtgärder som har påverkan för skyddsvärdet. Bestämmelser om skydd kan medföra att fastighetsägare har rätt till ersättning.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Kultur_Bebyggomr', '2016-03-07', '2016-03-07', '2e4eb367-d5df-45c4-ae0e-247662aee8d4', NULL, NULL, NULL, NULL, NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Administrativ', 'Planområdet', NULL, 'Genomförandetid', NULL, NULL, 'Genomförandetiden är [antal:heltal] år [laga kraft:text].', 'Exakt', 'Planens genomförandetid ska alltid anges. Tiden ska vara mellan 5 och 15 år.', 'Bestämmelser som gäller inom hela planområdet redovisas enbart bland planbestämmelserna.', 'PBL (2010:900)', '4', '21', NULL, NULL, 'Administrativa bestämmelser reglerar bland annat detaljplanens genomförandetid. Genomförandetiden ska vara 5 till 15 år.', NULL, NULL, '2015-01-02', NULL, 'DP_PO_Ad_Genomforande', '2017-03-01', '2016-03-07', '7ac64f60-e565-4d63-bff3-b8e3572c307b', NULL, NULL, NULL, NULL, NULL);
