INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Egenskap', 'Kvartersmark', NULL, 'Störningsskydd', NULL, NULL, '[skyddsatgard:text]', NULL, 'Bestämmelser om skydd mot störningar reglerar skydd mot de risker som kan uppstå på grund av de verksamheter som planen tillåter inom planområdet, åtgärder mot störningar som uppkommer utanför planområdet samt högsta tillåtna nivåer på störningar. Bestämmelser med krav på skyddsåtgärder bör endast tillämpas för att marken ska bli lämplig att bebygga. På allmän plats med enskilt huvudmannaskap bör sådana bestämmelser kombineras med administrativa bestämmelser om villkor för lov eller villkor för startbesked. Bestämmelser om högsta tillåtna nivåer på störning kan endast tillämpas om det finns särskilda skäl och nivåerna på störningarna inte är reglerade i annan lagstfiftning.', 'm#', 'PBL (2010:900)', '4', '12', '1', '1', 'Denna bestämmelse om störningsskydd ska används då ingen annan angiven egenskapsbestämmelse om störningsskydd i katalogen är tillämpbar.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Skydd_A', '2016-03-07', '2016-03-07', 'dc693d97-6e72-40e1-8661-5bf5c80524eb', NULL, NULL, NULL, NULL, NULL);
