INSERT INTO qdp2.bkatalog_imp VALUES ('2017-03-01', 'Administrativ', 'Planområdet', NULL, 'Ändrad lovplikt', NULL, NULL, 'Bygglov krävs inte för [atgard:text]. Bestämmelsen gäller under [tid:text].', NULL, 'Ändrad lovplikt kan ändras för vissa åtgärder. Om detta regleras är det viktigt att det tydligt framgår vilka åtgärder som omfattas. Ändrad lovplikt kan gälla under en begränsad tid. Om lovplikten minskas ska det av planbestämmelsen framgå under vilken tid bestämmelsen gäller. Det är lämpligt att tiden sammanfaller med genomförandetiden för detaljplanen, men den kan vara kortare eller längre.', 'Bestämmelser som gäller inom hela planområdet redovisas enbart bland planbestämmelserna.', 'PBL (2010:900)', '4', '15', '1', '1', 'Administrativa bestämmelser om ändrad lovplilkt tillämpas för att minska eller öka bygg-, rivnings- eller marklovplikten och bör alltid preciseras med vad som avses.', NULL, NULL, '2015-01-02', NULL, 'DP_PO_Ad_Bygglovplikt_Minskad', '2016-03-07', '2016-03-07', '7edc791d-70a4-4f9b-8836-39f797e4ca20', NULL, NULL, NULL, NULL, NULL);
