INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Användning', 'Allmän plats', 'Kommunalt', 'Gata', NULL, 'Gata', 'Gata', NULL, 'Gata är en generell användningsbestämmelse som bör tillämpas främst för gata avsedd för trafik inom en tätort eller trafik som har sitt mål vid gatan. Gata preciseras endast om det finns särskilt behov av det.', 'GATA', 'PBL (2010:900)', '4', '5', '1', '2', 'Gata är en generell användningsbestämmelse som bör tillämpas för områden för både fordonstrafik och gång- och cykeltrafik inom en tätort eller för trafik som har sitt mål vid gatan. En gata avgrenas från huvudnätet och ingår i lokalnätet med lägre framkomlighet och ofta många utfarter. I de flesta fall är det kommunen som är huvudman för gator.', 19, 'Ljust grå', '2015-01-02', NULL, 'DP_AP_Ko_Gata', '2018-08-01', '2016-03-07', '11e56478-2770-46bc-875a-af2702fa17e0', NULL, NULL, NULL, NULL, NULL);
