INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Användning', 'Allmän plats', 'Kommunalt', 'Park', NULL, 'Park', 'Park', NULL, 'Park är en generell användningsbestämmelse som bör tillämpas för grönområden som kräver skötsel och till viss del är anlagda. I användningen ingår även komplement för parkens användning.', 'PARK', 'PBL (2010:900)', '4', '5', '1', '2', 'Park är en generell användningsbestämmelse som bör tillämpas för grönområde som helt eller delvis är anlagt och som kräver skötsel. En park är främst avsedd för rekreation och promenader. Naturliga komplement i en park är exempelvis dammar, fontäner, skulpturer, paviljonger och mindre byggnader, för exempelvis restaurang, kioskverksamhet eller toaletter.', 20, 'Ljust grön', '2015-01-02', NULL, 'DP_AP_Ko_Park', '2016-03-07', '2016-03-07', '53e38f24-c91c-47db-a1ec-f57352ed894e', NULL, NULL, NULL, NULL, NULL);
