INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Användning', 'Allmän plats', 'Kommunalt', 'Natur', NULL, 'Natur', 'Naturområde', NULL, 'Natur är en generell användningsbestämmelse som bör tillämpas för friväxande grönområden som inte sköts mer än enligt skötselplan eller genom viss städning. Även mindre park-, vatten- och friluftsanläggningar och andra komplement för naturområdets användning ingår.', 'NATUR', 'PBL (2010:900)', '4', '5', '1', '2', 'Natur är en generell användningsbestämmelse som bör tillämpas för friväxande grönområde som inte sköts mer än enligt skötselplan eller genom viss städning. Inom ett naturområde kan mindre park-, vatten- eller friluftsanläggningar och andra komplement för naturområdets användning finnas.', 20, 'Ljust grön', '2015-01-02', NULL, 'DP_AP_Ko_Natur', '2016-03-07', '2016-03-07', '10b6f8e5-a84c-4603-b298-3631ebf93979', NULL, NULL, NULL, NULL, NULL);
