INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Användning', 'Kvartersmark', NULL, 'Besöksanläggningar', NULL, 'Besöksanläggningar', 'Besöksanläggningar', NULL, 'Besöksanläggningar är en generell användningsbestämmelse som bör tillämpas för områden för verksamheter som riktar sig till besökare. Här ingår kulturella och religiösa verksamheter, idrotts- och sportanläggningar med tillhörande byggnader och övriga besöksanläggningar. Med hänsyn till omgivningspåverkan bör användningen preciseras.', 'R', 'PBL (2010:900)', '4', '5', '1', '3', 'Besöksanläggningar är en generell användningsbestämmelse som bör tillämpas för områden för alla slag av besöksanläggningar. Användningsslaget är avsett att tillämpas för områden med verksamheter som riktar sig till besökare. Det kan exempelvis handla om kulturella och religiösa samlingslokaler, idrotts- och sportanläggningar med tillhörande byggnader osv. Med hänsyn till verksamheternas olika grad av omgivningspåverkan bör användningen preciseras.', 9, 'Orange', '2015-01-02', NULL, 'DP_KM_R', '2018-08-01', '2016-03-07', '2e335437-0777-43f6-9804-48b59d1c8ce0', NULL, NULL, NULL, NULL, NULL);
