INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Omfattning', 'Takvinkel', NULL, 'Minsta takvinkel är [takvinkel:decimaltal] grader', 'Min', 'Bestämmelser om takvinkel kan anges och reglerar takets lutning i grader. Takvinkeln kan anges antingen som en exakt vinkel, en minsta eller största vinkel eller med ett intervall mellan en minsta och en största vinkel.', NULL, 'PBL (2010:900)', '4', '11', '1', '1', 'Bestämmelser om takvinkel kan anges och reglerar takets lutning i grader. Takvinkeln kan anges antingen som en exakt vinkel, en minsta eller största vinkel eller med ett intervall mellan en minsta och en största vinkel.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Omfatt_Takvinkel_Min', '2018-08-01', '2016-03-07', '971aeeaa-f68a-4037-8aaa-9ebb9c141094', 'symbol', '2017-02-21T13:07:44.3546833', 26, 'Takvinkel', NULL);
