INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Fastighet', NULL, NULL, 'Minsta fastighetsstorlek är [storlek:decimaltal] kvadratmeter', 'Min', 'Bestämmelser om största och minsta fastighetsstorlek kan anges.', 'd#', 'PBL (2010:900)', '4', '18', '1', NULL, 'Bestämmelser om största och minsta fastighetsstorlek används för att styra exploateringsgraden inom ett område. Om fastighetsindelningen behöver regleras mer i detalj kan fastighetsbestämmelser, som är administrativa bestämmelser, användas.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Fastighet_Min_Fastighet', '2018-08-01', '2016-03-07', '4ca4281b-b3a7-4501-a642-252570a2eb07', NULL, NULL, NULL, NULL, NULL);
