INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Utförande', NULL, NULL, 'Dagvatten ska avledas till [utforande:text]', NULL, 'Bestämmelser om utförande reglerar byggnadsteknik och bör endast tillämpas när det krävs för att bebyggelsen ska kunna komma till stånd.', 'b#', 'PBL (2010:900)', '4', '16', '1', '1', 'Bestämmelser om utförande reglerar byggnadsteknik och bör endast tillämpas när det krävs för att bebyggelsen ska kunna komma till stånd. Ett visst tekniskt utförande av bebyggelsen kan behöva regleras med hänsyn till allmänna intressen eller med hänsyn till grannar. Bestämmelser som pekar ut en speciell teknik bör undvikas.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Utfor_Dagvatten', '2016-03-07', '2016-03-07', '508f3524-b9ad-4ccb-9794-c15ee8089371', NULL, NULL, NULL, NULL, NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Placering', NULL, NULL, 'Byggnad ska placeras minst [avstand:decimaltal] meter från tomtgräns', 'Min', 'Bestämmelser om placering reglerar byggnadsverks och tomters placering.', 'p#', 'PBL (2010:900)', '4', '16', '1', '1', 'Bestämmelser om placering av byggnadsverk och tomter används ofta för att reglera byggnaders placering på tomten i förhållande till grannfastigheter och i förhållande till gata och omgivning. I tät bebyggelse med små fastigheter är denna typ av bestämmelser viktiga för att få en fungerande bebyggelse. Det är viktigt att samordna placeringsbestämmelser med andra bestämmelser om exploatering och fastighetsindelning. Placering kan även regleras genom begränsning av markens utnyttjande för byggnader, till exempel prickmark.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Plac_Byggnad_Avstand_Tomtgrans', '2016-03-07', '2016-03-07', 'd18a54e1-375f-4955-8bd8-b1ed11f5fa79', NULL, NULL, NULL, NULL, NULL);
