INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Placering', NULL, NULL, 'Marken får inte förses med byggnad', NULL, 'Prickmark är en generell bestämmelse som anger att marken inte får förses med byggnad. Om det är nödvändigt med hänsyn till planens syfte kan typen av begränsning preciseras.', NULL, 'PBL (2010:900)', '4', '16', '1', '1', 'Prickmark är en bestämmelse som begränsar byggandet eller visar vad som får byggas inom ett område. Eftersom planer bör vara flexibla och användbara över en längre tid bör prickmark endast användas där det är nödvändigt.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Plac_Prickmark_Byggnad', '2016-03-07', '2016-03-07', 'aed19eed-e7da-41b3-9766-9f242b6c3dea', 'symbol', '2017-02-21T13:07:44.2826847', 18, 'Prickmark', NULL);
