INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Störningsskydd', NULL, NULL, '[dike:text]', NULL, 'Bestämmelser om skydd mot störningar reglerar skydd mot de risker som kan uppstå på grund av de verksamheter som planen tillåter inom planområdet, åtgärder mot störningar som uppkommer utanför planområdet samt högsta tillåtna nivåer på störningar. Bestämmelser med krav på skyddsåtgärder bör endast tillämpas för att marken ska bli lämplig att bebygga. På allmän plats med enskilt huvudmannaskap bör sådana bestämmelser kombineras med administrativa bestämmelser om villkor för lov eller villkor för startbesked. Bestämmelser om högsta tillåtna nivåer på störning kan endast tillämpas om det finns särskilda skäl och nivåerna på störningarna inte är reglerade i annan lagstfiftning.', 'm#', 'PBL (2010:900)', '4', '12', '1', '1', 'Bestämmelser om skyddsåtgärder för att motverka markförorening, olyckor, översvämning och erosion reglerar skydd mot de risker som kan uppstå på grund av de verksamheter som planen tillåter inom planområdet. Bestämmelser med krav på skyddsåtgärder tillämpas endast för att marken ska bli lämplig att bebygga. Om bestämmelser om skyddsåtgärder införs bör dessa kompletteras med bestämmelser om att lov eller startbesked, för en åtgärd som innebär väsentlig ändring av markens användning, inte kan ges förrän åtgärder som uppfyller skyddsbestämmelserna är utförda.', NULL, NULL, '2015-01-02', NULL, 'DP_KM_Eg_Skydd_Dike', '2016-03-07', '2016-03-07', '06c566ce-c4dc-4fec-9762-823c1dd601aa', NULL, NULL, NULL, NULL, NULL);
