INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Administrativ', 'Allmän plats', NULL, 'Huvudmannaskap', NULL, NULL, 'Huvudmannaskapet är enskilt för den allmänna platsen.', NULL, 'Kommunen kan bestämma att olika huvudmannaskap ska gälla för olika delar av detaljplanen. Endast enskilt huvudmannaskap anges som planbestämmelse och betecknas på plankartan med a följt av indexsiffra.', 'a#', 'PBL (2010:900)', '4', '7', NULL, NULL, 'Administrativa bestämmelser reglerar bland annat huvudmannaskap för allmänna platser. Kommunen ska var huvudman för allmän plats. Finns det särskilda skäl kan huvudmannaskapet vara enskilt.', NULL, NULL, '2015-01-02', NULL, 'DP_AP_Ad_Huvudman_En', '2016-03-07', '2016-03-07', '509ca598-9a1a-4ed7-bc1f-71e774428a87', NULL, NULL, NULL, NULL, NULL);
