INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Allmän plats', NULL, 'Utformning', NULL, NULL, 'Markytan får hårdgöras maximalt till en andel av [yta:decimaltal] procent', 'Exakt', 'Bestämmelser om utformning av allmän plats reglerar preciserat ändamål och utformning som ska eller kan finnas på den allmänna platsen.', 'infiltr#', 'PBL (2010:900)', '4', '5', '1', '2', 'Utformningsbestämmelser för allmän plats preciserar standarden på de användningar som lagts fast med användningsbestämmelser och tydliggör på så sätt vilken miljö planen garanterar och anger samtidigt utgångspunkterna för beräkning och fördelning av kostnader för anläggning och drift. Anläggningar och byggnader som behövs för den allmänna platsens skötsel och bruk och för gemensamma behov får finnas på allmän plats. Dessa redovisas så långt möjligt för att tydliggöra deras inverkan på miljön.', NULL, NULL, '2018-08-01', NULL, 'DP_AP_Eg_Utform_Hard_Max_Proc', '2018-08-01', '2018-08-01', '100e399f-9801-466e-ba4b-781e40b0b161', NULL, NULL, NULL, NULL, NULL);
