INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Utformning', NULL, NULL, 'Byggnad under mark', NULL, 'Bestämmelser om utformning reglerar delar på byggnadsverk, fasadutsmyckningar, material och kulörer på byggnadsverk samt typer av byggnader. Bestämmelser om utformning bör endast tillämpas om det finns särskilda skäl att ta hänsyn till förhållandena på platsen eller i omgivningen.', 'f#', 'PBL (2010:900)', '4', '16', '1', '1', 'Bestämmelser om utformning reglerar delar på byggnadsverk, fasadutsmyckningar, material och kulörer på byggnadsverk samt typer av byggnader. Utformningen kan vara kopplad till en önskan om ett visst uttryck på byggnader eller områden. I andra fall kan det gälla att tillvarata specifika förutsättningar på platsen. Bestämmelser om utformning bör endast tillämpas om det finns särskilda skäl att ta hänsyn till förhållandena på platsen eller omgivningen.', NULL, NULL, '2018-08-01', NULL, 'DP_KM_Eg_Utform_Byggnad_Under_Mark', '2018-08-01', '2018-08-01', '4f7399b0-4c49-4408-987f-bf1e180dc4c2', NULL, NULL, NULL, NULL, NULL);
