INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Lägenhetsfördelning', NULL, NULL, 'Lägenheter ska bestå av [antal rum:text]', NULL, 'Bestämmelser om lägenhetsfördelning och storlek på lägenheter reglerar andelen lägenheter av olika slag i bostadsbyggnader och lägenheternas storlek.', 'v#', 'PBL (2010:900)', '4', '11', '1', '3', 'Bestämmelser om lägenhetsfördelning och storlek på lägenheter reglerar andelen lägenheter av olika slag i bostadsbyggnader och lägenheternas storlek. Denna typ av bestämmelse används bara när det finns särskilda skäl. Ett särskilt skäl kan vara att det finns ett behov av exempelvis små lägenheter och att detta har uttryckts i kommunens riktlinjer för bostadsförsörjning.', NULL, NULL, '2018-08-01', NULL, 'DP_KM_Eg_Lagenhet_Antal_Rum', '2018-08-01', '2018-08-01', 'c61e1a7b-3d3c-42df-b7ae-1e2c2867cfd6', NULL, NULL, NULL, NULL, NULL);
