INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Kvartersmark', NULL, 'Utförande', NULL, NULL, 'Endast [fastighetsyta:decimaltal] % av fastighetsarean får hårdgöras', NULL, 'Bestämmelser om utförande reglerar byggnadsteknik och bör endast tillämpas när det krävs för att bebyggelsen ska kunna komma till stånd.', 'b#', 'PBL (2010:900)', '4', '16', '1', '1', 'Bestämmelser om utförande reglerar byggnadsteknik och bör endast tillämpas när det krävs för att bebyggelsen ska kunna komma till stånd. Ett visst tekniskt utförande av bebyggelsen kan behöva regleras med hänsyn till allmänna intressen eller med hänsyn till grannar. Bestämmelser som pekar ut en speciell teknik bör undvikas.', NULL, NULL, '2018-08-01', NULL, 'DP_KM_Eg_Utfor_Hardgora_Fast_Proc', '2018-08-01', '2018-08-01', '0b83381a-fa56-4ec3-9357-d11f77212b9d', NULL, NULL, NULL, NULL, NULL);
INSERT INTO qdp2.bkatalog_imp VALUES ('2018-08-01', 'Egenskap', 'Allmän plats', NULL, 'Utformning', NULL, NULL, 'Markytan får inte hårdgöras', NULL, 'Bestämmelser om utformning av allmän plats reglerar preciserat ändamål och utformning som ska eller kan finnas på den allmänna platsen.', 'infiltr#', 'PBL (2010:900)', '4', '5', '1', '2', 'Utformningsbestämmelser för allmän plats preciserar standarden på de användningar som lagts fast med användningsbestämmelser och tydliggör på så sätt vilken miljö planen garanterar och anger samtidigt utgångspunkterna för beräkning och fördelning av kostnader för anläggning och drift. Anläggningar och byggnader som behövs för den allmänna platsens skötsel och bruk och för gemensamma behov får finnas på allmän plats. Dessa redovisas så långt möjligt för att tydliggöra deras inverkan på miljön.', NULL, NULL, '2018-08-01', NULL, 'DP_AP_Eg_Utform_Ejhard', '2018-08-01', '2018-08-01', '4b5b559a-ed7a-4f07-b2e6-6e6fc785cac6', NULL, NULL, NULL, NULL, NULL);
